mapper.select:
SELECT refresh_token
FROM member
WHERE member.member_id = #{memberId}

SELECT email
FROM member
WHERE username = #{username}
  AND phone_number = #{phoneNumber}

mapper.update:
UPDATE member
SET nickname = #{nickname},
    receive_push_notification = #{receivePushNotification}
WHERE member_id = #{memberId}

UPDATE member
SET profile_image = #{profileImage}
WHERE member_id = #{memberId}

mapper.select:
SELECT * FROM member
WHERE social_type = #{socialType}
AND social_id = #{socialId}
AND status = 'ACTIVE'

mapper.insert:
INSERT INTO member (
    profile_image, username, level, email,
    social_type, social_id, role, status, is_new_member, created_at
) VALUES (
             #{profileImage}, #{username}, #{level}, #{email},
             #{socialType}, #{socialId}, 'USER', 'ACTIVE', #{isNewMember}, #{createdAt}
         )

mapper.select:
SELECT
    TIMESTAMPDIFF(YEAR, birth_date, CURDATE()) AS age FROM member m
WHERE m.member_id = #{memberId}
