mapper.select:
SELECT fp.product_id as "product_id", fp.fin_co_no as "finCoNo", fp.fin_prdt_cd as "fin_prdt_cd", fp.kor_co_nm as "kor_co_nm", fp.product_name as "product_name", c.name as "category_name", CAST(do.intr_rate AS CHAR) as "intr_rate", CAST(do.intr_rate2 AS CHAR) as "intr_rate2", CAST(do.save_trm AS CHAR) as "save_trm", do.intr_rate_type as "intr_rate_type", do.intr_rate_type_nm as "intr_rate_type_nm", dp.min_deposit as "min_deposit_amount", fp.external_link as "join_way" FROM financial_product fp JOIN product_category c ON fp.category_id = c.category_id JOIN deposit_product dp ON fp.product_id = dp.product_id JOIN deposit_option do ON dp.product_id = do.product_id
c.name = #{categoryName}
AND fp.subcategory_id = #{subcategoryId}
AND (fp.product_name LIKE CONCAT('%', #{searchText}, '%') OR fp.kor_co_nm LIKE CONCAT('%', #{searchText}, '%'))
AND do.intr_rate >= #{minIntrRate}
AND do.save_trm = #{saveTrm}
AND do.intr_rate_type = #{intrRateType}
AND fp.external_link LIKE CONCAT('%', #{joinWay}, '%')
AND dp.min_deposit <= #{minAmount} AND (dp.max_limit IS NULL OR dp.max_limit >= #{minAmount})
AND fp.kor_co_nm IN
#{bank}
OR fp.fin_co_no IN
#{bank}
ORDER BY do.intr_rate DESC
ORDER BY do.intr_rate ASC
ORDER BY do.save_trm DESC
ORDER BY do.save_trm ASC
ORDER BY do.intr_rate2 DESC
ORDER BY do.intr_rate2 ASC
ORDER BY (do.intr_rate * 0.846) DESC
ORDER BY (do.intr_rate * 0.846) ASC
ORDER BY fp.kor_co_nm DESC
ORDER BY fp.kor_co_nm ASC
ORDER BY fp.created_at DESC
LIMIT #{offset}, #{pageSize}

SELECT COUNT(*)
FROM financial_product fp
JOIN product_category c ON fp.category_id = c.category_id
JOIN deposit_product dp ON fp.product_id = dp.product_id
JOIN deposit_option do ON dp.product_id = do.product_id


        c.name = #{categoryName}



        AND fp.subcategory_id = #{subCategoryId}



        AND (fp.product_name LIKE CONCAT('%', #{searchText}, '%')
        OR fp.kor_co_nm LIKE CONCAT('%', #{searchText}, '%'))



        AND do.intr_rate >= #{minIntrRate}



        AND do.save_trm = #{saveTrm}



        AND do.intr_rate_type = #{intrRateType}



        AND fp.external_link LIKE CONCAT('%', #{joinWay}, '%')



        AND dp.min_deposit <= #{minAmount}
        AND (dp.max_limit IS NULL OR dp.max_limit >= #{minAmount})



        AND fp.kor_co_nm IN

            #{bank}
